SELECT film.film_id, film.title, film.release_year, film.rating FROM film JOIN film_category ON film_category.film_id=film.film_id JOIN category ON category.category_id=film_category.category_id JOIN film_actor ON film_actor.film_id=film.film_id JOIN actor ON actor.actor_id=film_actor.actor_id WHERE category.name = 'Horror' AND actor.first_name = 'SUSAN' AND actor.last_name = 'DAVIS' ORDER BY film.title ASC;